/* Event structure for event `myevent_unfrozen_order` */

/*!50106 DROP EVENT IF EXISTS `myevent_unfrozen_order`*/;

DELIMITER $$

/*!50106 CREATE DEFINER=`root`@`localhost` EVENT `myevent_unfrozen_order` ON SCHEDULE EVERY 180 SECOND STARTS '2015-12-28 15:13:44' ON COMPLETION NOT PRESERVE ENABLE DO BEGIN 
  -- **************** 每3分钟解冻冻结的金额 ********************
  -- 遍历属性
  DECLARE p_uid INT;
  DECLARE p_orderno VARCHAR(30);
  -- 是否出错
  DECLARE _err INT;
  -- 定义游标遍历数据结束标志
  DECLARE done INT DEFAULT 0;
  DECLARE cur_tran CURSOR FOR SELECT orderno,uid FROM ou_order WHERE STATUS=12 AND createtime<DATE_SUB(NOW(),INTERVAL 3 MINUTE);
  declare continue handler for not found set done=1; 
  -- 如果出现 sqlexception 则执行 rollback
  declare exit handler for sqlexception begin rollback;end;
  
  -- 打开游标
  OPEN cur_tran;
  -- 开始循环
  read_loop:LOOP
    FETCH cur_tran INTO p_orderno,p_uid;
    -- 如果读不到数据，则结束
    IF done THEN
      LEAVE read_loop;
    END IF;
    
    -- 开始事务
    START TRANSACTION;
      SET _err=0;
      UPDATE ou_order a SET STATUS=13,closetime=NOW() WHERE orderno=p_orderno;
      -- 更新订单状态 
      UPDATE o_order a INNER JOIN ou_orderinfo b ON a.orderno=b.orderno SET a.now_price=a.now_price-b.times,a.frozen_price=a.frozen_price-b.times;
      SELECT COUNT(1) INTO _err FROM o_order a WHERE a.now_price<0 OR a.frozen_price<0;
      IF _err=0 THEN
	COMMIT;
      ELSE 
        ROLLBACK;
      END IF; 
  END LOOP;
  CLOSE cur_tran;
END */$$
DELIMITER ;

