/* Procedure structure for procedure `pro_neworder` */

/*!50003 DROP PROCEDURE IF EXISTS  `pro_neworder` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `pro_neworder`(in p_sid int,OUT result VARCHAR(100))
label_pro:BEGIN
  -- 计算结束之后或者新建策略的时候，新增一张策略订单
  -- p_sid 策略id
  
  DECLARE v_orderno VARCHAR(22);
  -- 受影响行数
  DECLARE v_count INT DEFAULT 0;
  -- 下一期期数
  DECLARE v_ordernum INT DEFAULT 0;
  
  SELECT fun_getorderno() INTO v_orderno;
  -- 默认
  SET result='';
  
  -- 查询策略是否存在
  SELECT id INTO p_sid FROM p_strategy a WHERE a.id=p_sid AND a.status=0 AND NOW() BETWEEN a.begindate AND a.enddate;
  
  IF p_sid IS NULL OR p_sid<0 THEN
    SET result='策略已经失效';
    LEAVE label_pro;
  END IF;
  
  -- 查询下一期期数
  SELECT MAX(ordernum) INTO v_ordernum FROM o_order WHERE sid=p_sid;
  
  IF v_ordernum IS NULL OR v_ordernum=0 THEN
    SET v_ordernum=1;
  ELSE
    SET v_ordernum=v_ordernum+1;
  END IF;
  
  INSERT INTO o_order(orderno,pid,tid,sid,ordernum,STATUS,max_count,min_count,begin_price,prod_price,personNums,max_process,now_price)
  SELECT v_orderno,pid,tid,id,v_ordernum,0,max_count,min_count,begin_price,prod_price,personNums,max_process,0 FROM p_strategy WHERE id=p_sid;
  
  -- 查询订单创建是否成功
  SELECT COUNT(1) INTO v_count FROM o_order WHERE orderno=v_orderno;
  IF v_count IS NULL OR v_count!=1 THEN
    SET result='创建新订单失败';
    LEAVE label_pro;
  END IF;
  
  SET result='';
  
END */$$
DELIMITER ;

/* Procedure structure for procedure `pro_payorder` */

/*!50003 DROP PROCEDURE IF EXISTS  `pro_payorder` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `pro_payorder`(IN p_utime VARCHAR(24),IN p_paymoney DECIMAL,IN p_orderno VARCHAR(40),OUT result VARCHAR(100))
label_pro:BEGIN
  -- 支付成功之后
  -- 订单冻结金额和支付成功的金额是否一致，如果一致，next
  -- 转移 ou_order 和 ou_orderinfo 到 o_order 和 o_ordercode 和 o_orderuser 中，成功 next
  -- 解冻冻结金额，解冻成功 next
  -- 解冻成功之后，是否达到 100% 如果达到 100% ，更新状态，成功 next
  -- 新成订单，commit
  -- result='' 成功
  DECLARE c_orderno VARCHAR(40);
  DECLARE c_times INT;
  DECLARE c_maxcode INT;
  
  
  -- 循环下标
  DECLARE v_index INT;  
  -- 订单所属用户id
  DECLARE v_uid INT DEFAULT 0;
  -- 受影响行数
  DECLARE v_count INT DEFAULT 0;
  -- 订单状态
  DECLARE v_status INT DEFAULT -1;
  -- 订单总金额
  DECLARE v_money DECIMAL DEFAULT 0;
  -- 策略号
  DECLARE v_sid INT DEFAULT 0;
  -- 是否有已经可以计算的订单
  DECLARE v_calWinner INT DEFAULT 0;
  
  -- 定义游标遍历数据结束标志
  DECLARE done INT DEFAULT 0;
  DECLARE cur_tran CURSOR FOR SELECT orderno,times FROM ou_orderinfo WHERE ou_orderno=p_orderno;
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done=1; 
  
  -- 如果出现 sqlexception 则执行 rollback
  DECLARE EXIT HANDLER FOR SQLEXCEPTION BEGIN ROLLBACK; END;
  
  -- 默认值
  SET result='系统异常';
  
  -- 判断订单号
  IF p_orderno IS NULL OR p_orderno='' THEN
    SET result='支付订单号异常，请刷新重试';
    LEAVE label_pro;
  END IF;
  -- 判断时间
  IF p_utime IS NULL OR p_utime='' THEN
    SET result='支付时间异常';
    LEAVE label_pro;
  END IF;
  
  -- 查询订单
  SELECT COUNT(1),MAX(a.status),SUM(b.times),MAX(a.uid)  INTO v_count,v_status,v_money,v_uid  FROM ou_order a INNER JOIN ou_orderinfo b ON a.orderno=b.ou_orderno WHERE a.orderno=p_orderno;
  
  -- 判断订单产品数量  
  IF v_count IS NULL OR v_count<=0 THEN
    SET result='系统查询不到支付的订单，请刷新重新';
    LEAVE label_pro;
  END IF;
  -- 判断订单状态
  IF v_status IS NULL THEN
    SET result='订单状态异常'; 
    LEAVE label_pro;
  END IF;
  IF v_status=0 THEN 
    SET result='订单已经支付成功，请进行其他操作';
    LEAVE label_pro;
  END IF;
  IF v_status!=12 THEN
    SET result='订单状态异常';
    LEAVE label_pro;
  END IF;
  -- 判断金额有效性
  IF v_money IS NULL OR v_money<=0 OR v_money!=p_paymoney THEN
    SET result='订单金额有误';
    LEAVE label_pro;
  END IF;
  
  -- 开始事务
  START TRANSACTION;
  -- 改变订单
  UPDATE ou_order a SET a.utime=p_utime,a.status=0 WHERE a.orderno=p_orderno AND a.status=12;
  SELECT ROW_COUNT() INTO v_count;  
  
  IF v_count!=1 THEN
    ROLLBACK;
    SET result='订单已过期，请申请退单';
    LEAVE label_pro;
  END IF;
  
  -- 转移订单到 o_orderuser
  INSERT INTO o_orderuser(orderno,times,utime,ou_orderno,uid)
  SELECT b.orderno,b.times,a.utime,a.orderno,a.uid FROM ou_order a
  INNER JOIN ou_orderinfo b ON a.orderno=b.ou_orderno
  WHERE a.orderno=p_orderno;
  
  -- 是否有订单已经失效
  SELECT COUNT(1) INTO v_count FROM ou_orderinfo a INNER JOIN o_order b ON a.orderno=b.orderno AND b.status=0 WHERE a.ou_orderno=p_orderno;
  IF v_count IS NULL OR v_count=0 THEN
    ROLLBACK;
    SET result='幕拍订单状态无效，请申请退单';
    LEAVE label_pro;
  END IF;
  
  -- 循环幕拍号码
  -- 打开游标
  OPEN cur_tran;
  -- 开始循环
  read_loop:LOOP
    FETCH cur_tran INTO c_orderno,c_times;
    -- 如果读不到数据，则结束
    IF done THEN
      LEAVE read_loop;
    END IF;
    
    -- 查询最大编码
    SELECT MAX(ucode) INTO c_maxcode FROM o_ordercode WHERE orderno=c_orderno;
    IF c_maxcode IS NULL OR c_maxcode=0 THEN
      SET c_maxcode=100000;
    END IF;
    
    IF c_orderno IS NULL OR c_times IS NULL OR c_times<=0 THEN
      ROLLBACK;
      SET result='订单信息有误，请申请退单';
      LEAVE label_pro;
    END IF;
     
    -- 循环添加幕拍号
    SET v_index=c_times;
    WHILE v_index>0 DO
      INSERT INTO o_ordercode(orderno,uid,utime,ucode) VALUES (c_orderno,v_uid,p_utime,c_maxcode+v_index);
      SET v_index=v_index-1;
    END WHILE;
    
    -- 解冻金额
    UPDATE o_order a SET a.frozen_price=a.frozen_price-c_times WHERE a.orderno=c_orderno AND a.status=0 AND a.frozen_price-c_times>=0 AND a.frozen_price-c_times<a.now_price;
    SELECT ROW_COUNT() INTO v_count;
    
    IF v_count!=1 THEN
      ROLLBACK;
      SET result='解冻订单金额出错，请申请退单';
      LEAVE label_pro;
    END IF;
    
    -- 是否已经完成
    SELECT (CASE WHEN a.prod_price=a.now_price AND a.frozen_price=0 THEN 1 ELSE 0 END),a.sid INTO v_status,v_sid FROM o_order a WHERE a.orderno=c_orderno;
    IF v_status=1 THEN
      call pro_neworder(v_sid, @result);
      UPDATE o_order a SET a.endtime=NOW(),a.status=10 WHERE a.orderno=c_orderno;
      SELECT ROW_COUNT() INTO v_count;
      -- 更新订单状态
      IF v_count!=1 THEN
        ROLLBACK;
        SET result='更新订单状态出错';
        LEAVE label_pro;
      END IF;
      set v_calWinner=1;
    END IF;
  END LOOP;
  CLOSE cur_tran;
 
  -- 事务提交 
  SET result='';
  COMMIT;
  
  IF v_calWinner=1 THEN
    CALL pro_calWinner(@result);
  END IF;
END */$$
DELIMITER ;

/* Procedure structure for procedure `pro_winner` */

/*!50003 DROP PROCEDURE IF EXISTS  `pro_winner` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `pro_winner`(in p_orderno varchar(40),OUT result VARCHAR(100))
label_pro:BEGIN
  -- 计算获胜用户
  -- p_orderno [o_order订单号]
  DECLARE c_count INT;
  
  -- 最后 v_count 个时间和
  DECLARE v_time INT;
  -- 时间个数
  DECLARE v_timecount INT;
  -- 订单状态
  DECLARE v_status INT;
  -- 受影响行数
  DECLARE v_count INT;
  -- 中奖号码
  DECLARE v_wincode INT;
  
  -- 如果出现 sqlexception 则执行 rollback
  DECLARE EXIT HANDLER FOR SQLEXCEPTION BEGIN ROLLBACK; END;
  
  -- 默认值
  SET result='系统异常';
  
  -- 查询订单是否已经支付
  SELECT a.status INTO v_status FROM o_order a WHERE a.orderno=p_orderno;
 
  -- 判断订单 
  IF v_status IS NULL OR v_status NOT IN (10,11) THEN
    SET result='系统找不到可以计算的订单';
    LEAVE label_pro;
  END IF;
  
  -- 如果是 status=11 则是已经计算的了
  IF v_status=11 THEN
    SET result='';
    LEAVE label_pro;
  END IF;
  
  -- 否则开始计算
  SELECT SUM(utime) INTO v_time FROM (
    SELECT a.utime FROM (
      SELECT CAST(REPLACE(REPLACE(SUBSTRING(utime,12),':',''),'.','') AS SIGNED) utime FROM o_orderuser 
      WHERE utime<=(SELECT utime FROM o_orderuser WHERE orderno=p_orderno ORDER BY utime DESC LIMIT 0,1)
    ) a
    GROUP BY a.utime
    ORDER BY utime DESC LIMIT 0,50
  ) a;
  
  SELECT CAST(prod_price AS SIGNED) INTO v_timecount FROM o_order a WHERE a.orderno=p_orderno;
  
  -- 判断数值是否正确
  IF v_time IS NULL OR v_timecount IS NULL OR v_time=0 OR v_timecount=0 THEN
    SET result='计算中奖用户数值异常';
    LEAVE label_pro;
  END IF;
  
  SELECT v_time%v_timecount+100001 INTO v_wincode;
  
  -- 开始事务
  START TRANSACTION;
  
  -- 更新中奖用户
  UPDATE o_order a SET a.endno=v_wincode,a.status=11 WHERE a.status=10 AND a.orderno=p_orderno;
  SELECT ROW_COUNT() INTO v_count;
  IF v_count!=1 THEN
    ROLLBACK;
    SET result='更新中奖用户失败';
    LEAVE label_pro;
  END IF;
  
  -- 迁移往期订单
  INSERT INTO oo_order SELECT * FROM o_order a WHERE a.orderno=p_orderno;
  INSERT INTO oo_ordercode SELECT * FROM o_ordercode a WHERE a.orderno=p_orderno;
  INSERT INTO oo_orderuser SELECT * FROM o_orderuser a WHERE a.orderno=p_orderno;
  
  COMMIT;
  
  SET result='';
END */$$
DELIMITER ;

